import './Login.css';
import { useContext } from 'react';
import { User } from '../../App';
import { GoogleLogin } from 'react-google-login';
import FacebookLogin from 'react-facebook-login';
import LoginGithub from 'react-login-github';
import Axios from 'axios';

export default function Login() {
    const {user, setUser} = useContext(User);

    const googleAuth = (response) => {
        //Write Google Auth
        setUser({
            name: response.profileObj['name'],
            photo: response.profileObj['imageUrl'],
            email: response.profileObj['email'],
        });
        refreshGoogleTokenSetup(response);
    }

    const facebookAuth = (response) => {
        console.log(response);
        setUser({
            name: response.name,
            photo: response.picture.data.url,

        })
    }

    const githubAuth = (response) => {
        // Axios.post('https://github.com/login/oauth/access_token', {
        //     client_id: '02bc499969e28089f2ea',
        //     client_secret: '7cbb0d63b08705d91bacfe5ef4ea29310605d79f',
        //     code: response.code,
        // }).then(result => {
        //     console.log(result);
        // });
        console.log(response);
    }


    return (
        <div className="Login">
            <div className="loginHeaders">
                <h1>Welcome to MultiCoder</h1>
                <p className="greyish">Login and start kicking!</p>
            </div>
            <div className="loginBtns">
                <GoogleLogin 
                    clientId="299398573285-d7fo2pld42fqrn91r2dnfja1p30dkid3.apps.googleusercontent.com"
                    onSuccess={googleAuth}
                    onFailure={googleAuth}
                    cookiePolicy={'single_host_origin'}
                    isSignedIn={true}
                />
                <FacebookLogin 
                    appId="653395662493521"
                    fields="name,email,picture"
                    scope="email"
                    returnScopes={true}
                    callback={facebookAuth}
                    textButton="Login with Facebook"
                    icon={<i className="fab fa-facebook"></i>}
                />
                {/* <button onClick={githubAuth} className="loginBtn--github"><i class="fab fa-github"></i>Github</button> */}
                <LoginGithub 
                    clientId="02bc499969e28089f2ea"
                    onFailure={githubAuth}
                    onSuccess={githubAuth}
                    redirectUri="https://localhost:3000/login"
                    onRequest={githubAuth}
                    valid={true}
                />
            </div>
        </div>
    )
}


function refreshGoogleTokenSetup(res) {
    let refreshTiming = (res.tokenObj.expires_in || 3600 - 5 * 60) * 1000;
    const refreshToken = async() => {
        const newAuthRes = await res.reloadAuthResponse();
        refreshTiming = (newAuthRes.expires_in || 3600 - 5 * 60) * 1000;
        setTimeout(refreshToken, refreshTiming);
    };
    setTimeout(refreshToken, refreshTiming);
}
