import './Game.css';
import { useLocation } from 'react-router';
import { useEffect, useState } from 'react';
import AceEditor from "react-ace";

import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/theme-monokai";

export default function Game() {
    const { state } = useLocation();
    const [activeTab, setActiveTab] = useState('question');
    return (
        <div className="gameBoard">
            <div className="gameLeftSide">
                <div className="tabBar">
                    <div className="tabBarItem tabBarItem-active">Question</div>
                    <div className="tabBarItem">Console</div>
                    <div className="tabBarItem">Chat</div>
                </div>
                <div className="tabBarView" id="question" style={{display: (activeTab == 'question') ? 'block' : 'none'}}>
                    <h1>Question</h1>
                </div>
                <div className="tabBarView" id="console">
                    <h1>Console</h1>
                </div>
                <div className="tabBarView" id="chat">
                    <h1>Chat</h1>
                </div>
            </div>
            <div className="gameRightSide">
            <AceEditor
                mode="javascript"
                theme="monokai"
                name="UNIQUE_ID_OF_DIV"
                editorProps={{ $blockScrolling: true }}
                focus={true}
                enableBasicAutocompletion={true}
                // enableLiveAutocompletion={true}
                enableSnippets={true}
                width="100%"
                fontSize={16}
            />
            </div>
        </div>
    )
}
